-module(euler8).
-export([start/0]).

start() ->
  lists:max(start_acc("", [])).

start_acc(List, Acc) when length(List) >= 5 ->
  {Head, _} = lists:split(5, List),
  [_ | Tail] = List,
  start_acc(Tail, Acc ++ [product(Head)]);

start_acc(_, Acc) ->
  Acc.

product(List) ->
  product_acc(List, 1).

product_acc([], Acc) ->
  Acc;

product_acc(List, Acc) ->
  {Head, Tail} = lists:split(1, List),
  product_acc(Tail, erlang:list_to_integer(Head)*Acc).